import React, { useEffect, useState } from "react";

// import { useLocation } from "react-router-dom";

import { getData } from "../services/api";


const AddAdoption = () => {
    const [state, setState] = useState({
        approvedApplicants: [],
        approvedApplicantLastNameFragment: "",
        timeout: -1,
        latestApplication: {}
    });

    useEffect(() => {
        getData(`/applicant?status=approved`)
            .then(({ target }) => {
                setState({ ...state, approvedApplicants: JSON.parse(target.response) });
            })
            .catch(error => {
                console.log(error);
            })
        /* eslint-disable react-hooks/exhaustive-deps */
    }, []);

    const getLatestApplication = applicantId => {
        getData(`/application?applicantId=${applicantId}`)
            .then(({ target }) => {
                console.log(target);
                if (target.status === 200) {
                    setState({ ...state, latestApplication: JSON.parse(target.value) });
                } else {
                    console.log(target);
                }

            })
            .catch(error => {
                console.log(error);
            });
    }

    return (
        <>
            <h1>Add Adoption</h1>
            <form>
                <div className="form-unit">
                    <label htmlFor="approved-applicant-last-name">Search by approved applicant last name:</label>
                    <input
                        id="approved-applicant-last-name"
                        value={state.approvedApplicantLastNameFragment}
                        onChange={({ target }) => {
                            setState({ ...state, approvedApplicantLastNameFragment: target.value });
                            clearTimeout(state.timeout);
                            setState({
                                ...state, timeout: setTimeout(() => {
                                    // make http call for applicant
                                    getData(`/applicant?like=${state.approvedApplicantLastNameFragment}`)
                                        .then(({ target }) => {
                                            let approvedApplicants = JSON.parse(target.response);
                                            if (target.status === 200) {
                                                setState({ ...state, approvedApplicants });
                                            }
                                            else {
                                                console.log(target);
                                            }
                                        })
                                        .catch(error => {
                                            console.log(error);
                                        })
                                }, 400)
                            });
                        }}
                    />
                </div>

            </form>
            {/* <div>{JSON.stringify(useLocation())}</div> */}
            <h3>Select Applicant</h3>

            {state.approvedApplicants.length ? (
                <table>
                    <thead>
                        <tr>
                            <th></th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Street</th>
                            <th>City</th>
                            <th>State</th>
                            <th>Zip</th>
                            <th>Email</th>
                            <th>Phone Number</th>
                        </tr>
                    </thead>
                    <tbody>
                        {state.approvedApplicants.map((applicant, index) => {

                            return (
                                <tr key={applicant.id} >
                                    <td>
                                        <button type="button" onClick={() => {
                                            getLatestApplication(applicant.id);
                                        }}>Select</button>
                                    </td>
                                    <td>{applicant.firstName}</td>
                                    <td>{applicant.lastName}</td>
                                    <td>{applicant.street}</td>
                                    <td>{applicant.city}</td>
                                    <td>{applicant.state}</td>
                                    <td>{applicant.zip}</td>
                                    <td>{applicant.email}</td>
                                    <td>{applicant.phoneNumber}</td>
                                </tr>
                            );

                        })}
                    </tbody>

                </table>
            ) : (<h3>There are not any dogs currently in the shelter.</h3>)
            }
            {state.latestApplication.applicantId && (
                <div>{JSON.stringify(state.latestApplication)}</div>
            )}
        </>
    )


}

export default AddAdoption;
