import React, { useEffect, useState } from "react";

import { getData } from "../services/api";
import { isLoggedIn } from "../services/auth";
import { NavLinks } from "../components";

function getMonthName(monthNum) {
    switch (monthNum) {
        case 0: return "January"
        case 1: return "February"
        case 2: return "March"
        case 3: return "April"
        case 4: return "May"
        case 5: return "June"
        case 6: return "July"
        case 7: return "August"
        case 8: return "September"
        case 9: return "October"
        case 10: return "November"
        case 11: return "December"

        default: return "month"

    }
}

function GetMonthOffset(offset) {
    let today = new Date()
    today.setMonth(today.getMonth() + offset)

    return getMonthName(today.getMonth()) + " " + today.getFullYear()
}

const MonthlyAdoptionReport = () => {

    const [state, setState] = useState({
        data: {},
        errorMessage: "",
        loginRedirect: false,
        cookie: {}
    });

    // check auth
    useEffect(() => {
        const cookie = isLoggedIn();
        if (cookie.hasOwnProperty("isTrustedVolunteer") && cookie.isTrustedVolunteer) {
            setState({ ...state, cookie });
        }
        /* eslint-disable react-hooks/exhaustive-deps */
    }, []);

    useEffect(() => {
        // var i =0;
        // for ( i = -11; i <= -10; i++ )
        // {
            UpdateTables(-10)
        // }

    }, [state.cookie]);

    function UpdateTables(ind) {

        // if (state.cookie.hasOwnProperty("isTrustedVolunteer") && state.cookie.isTrustedVolunteer) {
        getData("/monthly-adoption-report?month=-10")
            .then(({ target }) => {
                if (target.status === 200) {

                    const dataDict = state.data
                    dataDict["10"] =JSON.parse(target.response)
                    // dataDict["10"].map(stuff =>{
                    //    window.alert (JSON.stringify(stuff))
                    // })
                    setState({ ...state, data:dataDict});
// window.alert("Updating")
// state.data["10"].map(stuff=>{
//     window.alert(JSON.stringify(stuff))
// })
                    // window.alert(JSON.stringify(state.data["10"]))
                } else {
                    setState({ ...state, errorMessage: target.response });
                }
            })
            .catch(error => {
                console.log(error);
                setState({ ...state, errorMessage: JSON.stringify(error) });
            })
        // }
    }

    return (
        <>
            {/*<NavLinks isTrustedVolunteer={state.cookie.isTrustedVolunteer} />*/}
            <h1>Monthly Adoption Report</h1>
            <p>{GetMonthOffset(-18)}</p>
            <table>
                <thead>
                    <td>Breed</td>
                    <td>Surrender Count</td>
                    <td>Adoption Count</td>
                    <td>Expenses</td>
                    <td>Adoption Fees</td>
                    <td>Profit</td>
                </thead>
                <tbody>
                    {state.data.length && (
                        state.data["10"].map(datum => {
                            return (
                                <tr>
                                    <td>{datum.toString()}</td>
                                    <td>{datum.breed}</td>
                                    <td>{datum.SurrenderCount.Float64}</td>
                                    <td>{datum.AdoptionCount.Float64}</td>
                                    <td>{datum.Expenses.Float64}</td>
                                    <td>{datum.AdoptionFees.Float64}</td>
                                    <td>{datum.Profit.Float64}</td>
                                </tr>
                            )
                        })
                    )}
                </tbody>
            </table>

            {state.errorMessage && (
                <div styles={{ color: "red" }}>{state.errorMessage}</div>
            )}
        </>
    );
}

export default MonthlyAdoptionReport;
