import React, { useEffect, useState } from "react";

import { getData } from "../services/api";

const VolunteerLookup = () => {

    const [volunteerName, setVolunteerName] = useState("");

    const [state, setState] = useState({
        data: [],
        errorMessage: "",
        timeout: -1
    });

    useEffect(() => {
        // replace "/volunteer" with correct endpoint name
        getData("/volunteer")
            .then(({ target }) => {
                console.log(target)
                if (target) {
                    setState({ ...state, data: JSON.parse(target.response) });
                } else {
                    setState({ ...state, errorMessage: target.response });
                }
            })
            .catch(error => {
                console.log(error);
                setState({ ...state, errorMessage: JSON.stringify(error) });
            })
        /* eslint-disable react-hooks/exhaustive-deps */
    }, []);

    const getVolunteersLike = () => {
        getData(`/volunteer?like=${volunteerName}`)
            .then(({ target }) => {
                if (target.status === 200) {
                    const data = JSON.parse(target.response)
                    console.log(data);
                    setState({ ...state, data });

                }
            })
            .catch(error => {
                console.log(error);
            })
            .finally(() => {
                console.log(state.data);
            })
    };

    console.log(state.data);

    return (
        <>
            <h1>Volunteer Lookup</h1>
            <div className="form-unit">
                <label htmlFor="volunteer-name">Volunteer name:</label>
                <input
                    type="text"
                    value={volunteerName}
                    onChange={({ target }) => {
                        setVolunteerName(target.value);
                        clearTimeout(state.timeout);
                        const timeout = setTimeout(() => {
                            getVolunteersLike()
                        }, 600)
                        setState({ ...state, timeout });
                    }}
                />
            </div>

            {
                state.data.length ? (

                    <table>
                        <thead>
                            <tr>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Email</th>
                            <th>Phone Number</th>
                            </tr>
                        </thead>
                        <tbody>
                        {
                            state.data.map(volunteer => {
                                return
                                <tr>
                                    <th>volunteer.</th>
                                </tr>
                            })
                        }
                        </tbody>
                    </table>
                ) : (
                    <p> No Volunteers</p>)


            }
            {state.errorMessage && (
                <div styles={{ color: "red" }}>{state.errorMessage}</div>
            )}
        </>
    );
}

export default VolunteerLookup;

